mapper.select:

select

from cc_symbol
where id = #{id,jdbcType=INTEGER}
and symbol_id = #{symbolId,jdbcType=VARCHAR}

mapper.delete:

delete from cc_symbol
where id = #{id,jdbcType=INTEGER}
and symbol_id = #{symbolId,jdbcType=VARCHAR}

mapper.insert:

insert into cc_symbol (id, symbol_id, exchange_id,
symbol_type, asset_id_base, asset_id_quote
)
values (#{id,jdbcType=INTEGER}, #{symbolId,jdbcType=VARCHAR}, #{exchangeId,jdbcType=VARCHAR},
#{symbolType,jdbcType=VARCHAR}, #{assetIdBase,jdbcType=VARCHAR}, #{assetIdQuote,jdbcType=VARCHAR}
)

mapper.update:

update cc_symbol
set exchange_id = #{exchangeId,jdbcType=VARCHAR},
symbol_type = #{symbolType,jdbcType=VARCHAR},
asset_id_base = #{assetIdBase,jdbcType=VARCHAR},
asset_id_quote = #{assetIdQuote,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
and symbol_id = #{symbolId,jdbcType=VARCHAR}
